import React from "react";
import { useLocation } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { useDispatch } from "react-redux";
import { addItem } from "../slices/cartSlice";

// Displays product items with "Buy" button to add to cart
export default function Products() {
  const location = useLocation();
  const state = location.state;
  const username = state?.username || localStorage.getItem("username");
  const dispatch = useDispatch();

   // Array of Products
   const items = [
    {
      img: "https://pngimg.com/d/headphones_PNG7645.png",
      title: "Wireless Headphones",
      price: 99.99,
      description: "High-quality Bluetooth headphones with noise cancellation.",
    },
    {
      img: "https://png.pngtree.com/png-vector/20211026/ourmid/pngtree-mobile-phone-holder-suction-cup-communication-adjustment-portable-png-image_4007453.png",
      title: "Smartphone Stand",
      price: 15.49,
      description: "Adjustable stand for phones and small tablets.",
    },
    {
      img: "https://img.freepik.com/free-vector/composition-with-realistic-usb-3-0-charging-cable-mobile-devices_1284-54742.jpg",
      title: "USB-C Charger",
      price: 25.0,
      description: "Fast-charging USB-C power adapter for multiple devices.",
    },
    {
      img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQBbauO4pO772SQFN8F3D0AJyQDuC5B1c9YYMpuBm55Pkq1yxSh89zCojV0Un1-BrQAmkw&usqp=CAU",
      title: "Gaming Mouse",
      price: 49.95,
      description:
        "Ergonomic mouse with RGB lighting and customizable buttons.",
    },
    {
      img: "data:image/jpeg;base64,",
      title: "4K Monitor",
      price: 299.99,
      description: "Ultra HD display with vibrant color and fast refresh rate.",
    },
    {
      img: "data:image/jpeg;base64,",
      title: "Mechanical Keyboard",
      price: 89.95,
      description: "Backlit keyboard with blue switches and metal frame.",
    },
    {
      img: "data:image/jpeg;base64,",
      title: "Laptop Backpack",
      price: 45.0,
      description: "Water-resistant backpack with padded laptop compartment.",
    },
    {
      img: "data:image/jpeg;base64,",
      title: "Webcam 1080p",
      price: 39.99,
      description: "Full HD webcam with built-in microphone.",
    },
    {
      img: "data:image/jpeg;base64,",
      title: "Portable SSD 1TB",
      price: 129.0,
      description: "Compact solid-state drive with USB 3.2 for fast transfers.",
    },
    {
      img: "data:image/jpeg;base64,",
      title: "Smart LED Light Bulb",
      price: 12.99,
      description:
        "Color-changing bulb controllable via app or voice assistant.",
    },
  ];

  // Adds item to Redux cart
  const handleBuy = (item) => {
    dispatch(addItem(item));
    alert(`${item.title} added to cart.`);
  };

  // Map each item to a Bootstrap card
  const itemsList = items.map((item) => (
    <Card style={{ width: "18rem", margin: "1rem" }} key={item.title}>
      <Card.Img variant="top" src={item.img} alt={item.title} />
      <Card.Body>
        <Card.Title>{item.title}</Card.Title>
        <Card.Text>${item.price.toFixed(2)}</Card.Text>
        <Card.Text>{item.description}</Card.Text>
        <Button onClick={() => handleBuy(item)}>Buy</Button>
      </Card.Body>
    </Card>
  ));

  return (
    <div>
      {username ? (
        <>
          <h2>Welcome to the store</h2>
          <div style={{ display: "flex", flexWrap: "wrap" }}>{itemsList}</div>
        </>
      ) : (
        <p>Please log in to view products.</p>
      )}
    </div>
  );
}